#pragma once

namespace Aspose {

namespace Pdf {

/// <summary>
/// This action represents actions for conversion of images with soft mask. 
/// </summary>
enum class ConvertSoftMaskAction
{
    /// <summary>
    /// Use default strategy.
    /// </summary>
    Default,
    /// <summary>
    /// Soft mask will be converted into stencil mask.
    /// </summary>
    ConvertToStencilMask
};

} // namespace Pdf
} // namespace Aspose


