namespace Aspose {

namespace Pdf {

/// <summary>
/// Defines the available serialization styles for emphasis and strong emphasis.
/// For specification see CommonMark - Emphasis and strong emphasis.
/// </summary>
enum class EmphasisStyle
{
    /// <summary>
    /// Use '*' respectively '**' for (strongly) emphasized text.
    /// </summary>
    Asterisk = 0,
    /// <summary>
    /// Use '_' respectively '__' for (strongly) emphasized text.
    /// </summary>
    Underscore = 1
};

} // namespace Pdf
} // namespace Aspose


