namespace Aspose {

namespace Pdf {

namespace Text {

/// <summary>
/// Text CoordinateOrigin enumeration.
/// </summary>
enum class CoordinateOrigin
{
    /// <summary>
    /// The lowest coordinate of text should be treated as the baseline.
    /// </summary>
    BaseLine,
    /// <summary>
    /// The lowest coordinate of text should be treated as the descender line.
    /// </summary>
    Descender
};

} // namespace Text
} // namespace Pdf
} // namespace Aspose


